 * @package error.php
 */

/**
 * Settings
 */
$title		= ""; //The title that is displayed next to the error title e.g. (404 not found[ .::. TOAOS])
$redirect	= "/"; //Redirect if the error ID is not set

/**
 * Script
 */
if(isset($_GET['id']) && is_numeric($_GET['id'])) {
	
	switch ($_GET['id']) {
		case '401':
			$code		= '401';
			$msg		= '401 Not authorized';
			$longmsg	= 'Your not authorized to view this file.';
		break;
		case '403':
			$code		= '403';
			$msg		= '403 Forbidden';
			$longmsg	= 'Forbidden';
		break;
		case '404':
			$code		= '404';
			$msg		= '404 Not found';
			$longmsg	= 'The requested URL ' . $_SERVER['REDIRECT_URL'] . ' was not found on this server.';
		break;
		case '408':
			$code		= '408';
			$msg		= '408 Request timeout';
			$longmsg	= 'Request Timeout, try reloading the page.';
		break;
		case '500':
			$code		= '500';
			$msg		= '500 Internal server error';
			$longmsg	= 'Internal server error, try reloading the page or go back.';
		break;
		case '504':
			$code		= '504';
			$msg		= '504 Gateway timeout';
			$longmsg	= 'Gateway Timeout, try reloading the page.';
		break;
		default:
			$code		= 'Error';
			$msg		= 'Error';
			$longmsg	= 'Something went wrong :(';
		break;
	}
	
	exit("<!DOCTYPE html><html><head><meta name=\"robots\" content=\"noindex,follow\" /><title>$msg $title</title><style type=\"text/css\">body{margin:0;padding:0;font:13px/1.4 \"Lucida Grande\", Lucida, sans-serif;}#error {background-color:#f1f1f1;width:70%;height:50%;margin:60px auto 20px;padding:20px;border:1px solid #d5d5d5;-webkit-border-radius:10px;-moz-border-radius:10px;border-radius:10px;}#error:after{content:".";display:block;height:0;clear:both;visibility:hidden;}#error img{width:64px;height:58px;float:left;}h1{color:#535353;font-size:18px;margin:2px 0 13px 85px;}p{margin:13px 0 0 85px;}</style></head><body><div id=\"error\"><img src=\"data:image/png;base64, \" /><h1>$code</h1><p>$longmsg</p></div></body></html>");
	
} else {
	header("Location: $redirect");
	exit;
}
